from cassandra.cluster import Cluster
from cassandra.auth import PlainTextAuthProvider
from cassandra.cqlengine.connection import register_connection, set_default_connection
import pathlib
from . import config

CQLENG_ALLOW_SCHEMA_MANAGEMENT=1

# This secure connect bundle is autogenerated when you download your SCB, 
# if yours is different update the file name below


# This token JSON file is autogenerated when you download your token, 
# if yours is different update the file name below

BASE_DIR = pathlib.Path(__file__).parent
CLUSTER_BUNDLE = str(BASE_DIR / "ignored" / "secure-connect-fitted.zip")

settings = config.get_settings()
CLIENT_ID = settings.db_client_id
CLIENT_SECRET = settings.db_client_secret



def get_cluster():
   cloud_config= {
    'secure_connect_bundle': CLUSTER_BUNDLE
    }
   auth_provider = PlainTextAuthProvider(CLIENT_ID, CLIENT_SECRET)
   cluster = Cluster(cloud=cloud_config, auth_provider=auth_provider)
   return cluster

def get_session():
    cluster = get_cluster()
    session = cluster.connect()
    register_connection(name=str(session), session=session)
    set_default_connection(str(session))
    return session

